"use strict";var buttons={makeButton:function(e,t){e.addEventListener("click",t)},initVidBtns:function(e,t){var n=e.querySelector("#play-pause");this.makeButton(n,function(){t.paused?(t.play(),n.innerHTML="Pause"):(t.pause(),n.innerHTML="Play")});var u=e.querySelector("#mute");this.makeButton(u,function(){t.muted?(t.muted=!1,u.innerHTML="Mute"):(t.muted=!0,u.innerHTML="Unmute")});var a=e.querySelector("#volume-bar");a.addEventListener("change",function(){t.volume=a.value})}};module.exports=buttons;